/**
 * @hidden
 */
export function getRef(comp, refName, customRef) {
    return comp.v3 ? comp[refName + "Ref"] : comp.$refs["" + (customRef || refName)];
}
/**
 * @hidden
 */
export function setRef(comp, refName, customRef) {
    return (comp.v3 ? function (el) {
        comp[refName + "Ref"] = el;
    } : customRef || refName);
}
